package ForLoop;

public class ForEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=10;i++)
		{
	    System.out.println(i);
		}

		//printing in reverse order
		System.out.println("printing in reverse order:");
		for(int j=10;j>=1;j--)
		{
			System.out.println(j);
		}
	}

}
