package basic;

public class Addition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10 , b=11 , c,c11,c22,c33,c44;
		c=a+b;
		c11=a-b;
		c22=a*b;
		c33=a/b;
		c44=a%b;
		System.out.println("Addition="+c);
		System.out.println("Subtraction="+c11);
		System.out.println("Multiplication="+c22);
		System.out.println("Division="+c33);
		System.out.println("Percent="+c44);
		
		
	}

}
